import React, { Component } from 'react'

class Toggle extends Component {

    constructor(props) {
        super(props);
        this.state = {
          status: props.status || false
        };
      }

      toggleSwitch = () => {
        this.setState(
        ...this.state, {
          status: !this.state.status
        }, () => {
          this.callbackEvent();
        });
      }

      callbackEvent = () => {
        if(this.props.onClick) {
          this.props.onClick(this.props.name, this.state.status);
        }
      }

    render() {
        return (
<div className="container">
          <ToggleSwitch type={SwitchType.Square}/>
          <ToggleSwitch type={SwitchType.Rounded}/>
          <ToggleSwitch type={SwitchType.Stripped}/>
      </div>
        )

    }
}

export default Toggle;
